public class Question13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=1;
		for(int i=1;i>0;i=i*2)
		{
			
				if(i==1)
				{
					System.out.print(num+"+");
				}
				else
				{
					System.out.print(num+"/"+i+"+");
				}
			
			
			
			
		}
	}

}
